mapper.update:

 	UPDATE B_PAGEBUY 
 	SET PISREFUND=''
 	WHERE id=#{id} AND pageNO = #{pageNO}

mapper.select:

	SELECT count(pageNO)
	FROM B_PAGEBUY 
	WHERE id=#{id} AND pageNO=#{pageNO}

mapper.insert:
 
  
 	SELECT pageNO FROM b_page WHERE pageDate = #{pageDate}	
  
 
 
    INSERT INTO B_PAGEBUY (PAGEPAY, ID, PAGENO, PAGEPAYDATE, PISREFUND) 
	VALUES( 0, #{id}, #{pageNO}, #{pageDate}, '')
